target.script:
var excluded = ' ' + project.getProperty('author-validation.excluded')
        .split(' ')
        .filter(function(s) { return s.trim().length>0; })
        .map(function(s) { return '--exclude=' + s.trim(); })
        .join(' ');

project.setProperty(
    'author-validation.arguments',
    ((project.getProperty('author-validation.php-files') == 'true') ? ' --php-files' : '') +
    ((project.getProperty('author-validation.composer')  == 'true') ? ' --composer'  : '') +
    ((project.getProperty('author-validation.bower')     == 'true') ? ' --bower'     : '') +
    ((project.getProperty('author-validation.packages')  == 'true') ? ' --packages'  : '') +
    ((excluded.length>0                                  == true)   ? excluded       : '')
);
